        #region GetFileInfosAsync
        public Task<FileInfoResponse> GetFileInfosAsync(FileInfoRequest request)
        {
            return Task.Run(() =>
            {
                var fileInfoResponse = new FileInfoResponse { FileInfos = new List<TextFileInfo>(), Messages = new List<String>() };

                try
                {
                    if (Directory.Exists(request.Path))
                    {
                        request.SearchPattern = String.IsNullOrWhiteSpace(request.SearchPattern) ? "*.txt" : request.SearchPattern;
                        if (!String.IsNullOrWhiteSpace(request.Path))
                        {
                            var directoryInfo = new DirectoryInfo(request.Path);

                            if (directoryInfo.Exists)
                            {
                                var fileInfos = new List<FileInfo>();

                                fileInfos.AddRange(directoryInfo.EnumerateFiles(request.SearchPattern).Where(o => !o.Name.EndsWith("_ISSUES.TXT")).ToList());

                                fileInfoResponse.FileInfos.AddRange(fileInfos.Select(fileInfo => new TextFileInfo
                                {
                                    Name          = fileInfo.Name,
                                    Extension     = fileInfo.Extension,
                                    Exists        = fileInfo.Exists,
                                    DirectoryName = fileInfo.DirectoryName,
                                    FullName      = fileInfo.FullName,
                                    CreationTime  = fileInfo.CreationTime,
                                    LastWriteTime = fileInfo.LastWriteTime,
                                    Length        = fileInfo.Length, 
                                    Size          = FormatBytes(fileInfo.Length)
                                }));
                            }
                        }
                        else
                        {
                            fileInfoResponse.Messages?.Add("No path provided");
                        }
                    }
                    else
                    {
                        fileInfoResponse.Messages?.Add($"Path provided does not exist. { request.Path }");
                    }
                }
                catch (ArgumentNullException anex)
                {
                    fileInfoResponse.Messages?.Add(anex.Message);
                }
                catch (ArgumentException aex)
                {
                    fileInfoResponse.Messages?.Add(aex.Message);
                }
                catch (Exception ex)
                {
                    fileInfoResponse.Messages?.Add(ex.Message);
                }

                return fileInfoResponse;
            });
        }
        #endregion GetFileInfosAsync

        #region GetGnmaTextFileAsync
        public Task<GnmaTextFileResponse> GetGnmaTextFileAsync(FileInfoRequest request)
        {
            return Task.Run(() =>
            {
                var gnmaTextFileResponse = new GnmaTextFileResponse { Messages = new List<String>() };

                try
                {
                    if (System.IO.File.Exists(request.FullPathAndFileName))
                    {
                        gnmaTextFileResponse.FileName = new FileInfo(request.FullPathAndFileName).Name;
                        gnmaTextFileResponse.Lines = System.IO.File.ReadAllLines(request.FullPathAndFileName).ToList();
                    }
                    else
                    {
                        gnmaTextFileResponse.Messages?.Add($"The File or Path provided does not exist. { request.FullPathAndFileName }");
                    }
                }
                catch (UnauthorizedAccessException uex)
                {
                    gnmaTextFileResponse.Messages?.Add(uex.Message);
                }
                catch (NotSupportedException nex)
                {
                    gnmaTextFileResponse.Messages?.Add(nex.Message);
                }
                catch (ArgumentNullException anex)
                {
                    gnmaTextFileResponse.Messages?.Add(anex.Message);
                }
                catch (ArgumentException aex)
                {
                    gnmaTextFileResponse.Messages?.Add(aex.Message);
                }
                catch (Exception ex)
                {
                    gnmaTextFileResponse.Messages?.Add(ex.Message);
                }

                return gnmaTextFileResponse;
            });
        }
        #endregion GetGnmaTextFileAsync

