        #region SaveGnmaTextFileAsync
        public Task<GnmaTextFileResponse> SaveGnmaTextFileAsync(SaveGnmaTextFileRequest request)
        {
            return Task.Run(() =>
            {
                var response = new GnmaTextFileResponse { Messages = new List<String>() };

                try
                {
                    if (request.Lines != null && request.Lines.Any())
                    {
                        System.IO.File.WriteAllLines(request.FullPathAndFileName, request.Lines);
                        response.IsSuccessful = true;
                    }
                    else
                    {
                        response.Messages.Add("No Text Lines were provided to save.");
                    }
                }
                catch (UnauthorizedAccessException uex)
                {
                    response.Messages?.Add(uex.Message);
                }
                catch (NotSupportedException nex)
                {
                    response.Messages?.Add(nex.Message);
                }
                catch (DirectoryNotFoundException dex)
                {
                    response.Messages?.Add(dex.Message);
                }
                catch (ArgumentNullException anex)
                {
                    response.Messages?.Add(anex.Message);
                }
                catch (ArgumentException aex)
                {
                    response.Messages?.Add(aex.Message);
                }
                catch (PathTooLongException pex)
                {
                    response.Messages?.Add(pex.Message);
                }
                catch (IOException ioex)
                {
                    response.Messages?.Add(ioex.Message);
                }
                catch (Exception ex)
                {
                    response.Messages?.Add(ex.Message);
                }

                return response;
            });
        }
        #endregion SaveGnmaTextFileAsync

