        #region FormatBytes
        public static string FormatBytes(long length)
        {
            var formattedSize = String.Empty;

            string[] suffix = { "B", "KB", "MB", "GB", "TB", "PB", "EB" };

            if (length == 0)
            {
                formattedSize = $"0 {suffix[0]}";
            }
            else
            {
                var bytes = length;
                var place = Convert.ToInt32(Math.Floor(Math.Log(bytes, 1024)));
                var size  = Math.Round(bytes / Math.Pow(1024, place), 1).ToString("#.0");

                formattedSize = $"{size} {suffix[place]}";
            }

            return formattedSize;
        }
        #endregion FormatBytes

